import geopandas as gpd; import os; shapefile_path = os.path.join("temp_boundaries", "statistical-gis-boundaries-london", "ESRI", "London_Borough_Excluding_MHW.shp"); gdf = gpd.read_file(shapefile_path); gdf = gdf.to_crs("EPSG:4326"); os.makedirs(os.path.join("assets", "geojson"), exist_ok=True); output_path = os.path.join("assets", "geojson", "london_boroughs.geojson"); gdf.to_file(output_path, driver="GeoJSON"); print(f"转换完成！GeoJSON文件已保存到: {output_path}"); print(f"包含的行政区数量: {len(gdf)}"); print("\n行政区列表:"); [print(f"- {name}") for name in sorted(gdf["NAME"].unique())]
